    private void jTable_NounMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jTable_NounMouseClicked
        int Tab_index = jTabbedPane2.getSelectedIndex();
        int row = jTable_Noun.getSelectedRow();
        int column = jTable_Noun.getSelectedColumn();
        String value = jTable_Noun.getValueAt(row, column).toString();

        System.out.println("Title: "+value+" row "+row+" column "+column);

        if(Tab_index==1)
        {
            try
            {
                jLabel_noun.setText(jTable_Noun.getValueAt(row, 1).toString());
                jTextField_animate.setText(jTable_Noun.getValueAt(row, 2).toString());
                jTextField_human.setText(jTable_Noun.getValueAt(row, 3).toString());
                jTextField_gender.setText(jTable_Noun.getValueAt(row, 4).toString());
                jTextField_alive.setText(jTable_Noun.getValueAt(row, 5).toString());
                jTextField_agent.setText(jTable_Noun.getValueAt(row, 6).toString());
                jTextField_honor.setText(jTable_Noun.getValueAt(row, 7).toString());
                jTextField_intlligent.setText(jTable_Noun.getValueAt(row, 8).toString());
                jTextField_adult.setText(jTable_Noun.getValueAt(row, 9).toString());
                jTextField_leg.setText(jTable_Noun.getValueAt(row, 10).toString());
                jTextField_hand.setText(jTable_Noun.getValueAt(row, 11).toString());
                jTextField_wing.setText(jTable_Noun.getValueAt(row, 12).toString());

            }

            catch (Exception ex)
            {
                JOptionPane.showMessageDialog(null, ex);
            }

        }

    }//GEN-LAST:event_jTable_NounMouseClicked

